query TitleImages($id: ID!, $before: ID, $after: ID, $jumpTo: ID, $first: Int, $last: Int, $lastYes: Boolean!, $firstYes: Boolean!) {
  title(id: $id) {
    titleText {
      text
      __typename
    }
    meta {
      publicationStatus
      __typename
    }
    releaseYear {
      year
      __typename
    }
    images(first: $first, after: $after, jumpTo: $jumpTo) @include(if: $firstYes) {
      total
      ...MediaViewerMeta
      __typename
    }
    wrapFront: images(last: $last, before: $before) @include(if: $lastYes) {
      total
      ...MediaViewerMeta
      __typename
    }
    wrapBack: images(first: $first) @include(if: $firstYes) {
      total
      ...MediaViewerMeta
      __typename
    }
    __typename
  }
}

fragment MediaViewerMeta on ImageConnection {
  pageInfo {
    endCursor
    hasNextPage
    hasPreviousPage
    startCursor
    __typename
  }
  edges {
    position
    cursor
    node {
      ...MediaViewerImageMeta
      ...MediaSheetImageMeta
      __typename
    }
    __typename
  }
}

fragment MediaViewerImageMeta on Image {
  id
  url
  height
 type
  width
  caption {
    plainText
    __typename
  }
}

fragment MediaSheetImageMeta on Image {
  copyright
  createdBy
  caption {
    plaidHtml
    __typename
  }
  titles {
    id
    titleText {
      text
      __typename
    }
    __typename
  }
  source {
    attributionUrl
    text
    banner {
      url
      attributionUrl
      __typename
    }
    __typename
  }
  names {
    id
    nameText {
      text
      __typename
    }
    __typename
  }
  countries {
    text
    __typename
  }
  languages {
    text
    __typename
  }
  correctionLink(relatedId: $id, contributionContext: {isInIframe: true, returnUrl: "https://www.imdb.com/close_me", business: "consumer"}) {
    url
    __typename
  }
  reportingLink(relatedId: $id, contributionContext: {isInIframe: true, returnUrl: "https://www.imdb.com/close_me", business: "consumer"}) {
    url
    __typename
  }
}
